import React from "react";


class Users extends React.Component {

         users = [
         { // это массив, в котором есть 2 объекта
                  id: 1, /*id, Name и пр. это ключи*/ 
                  Name: 'Tommas',
                  Surname: 'Bang',
                  Email: 'tombang@yandex.ru',
                  Age: 34,
                  Married: true
         },
         {
                  id: 2,
                  Name: 'Nickole',
                  Surname: 'Vee',
                  Email: 'nickkieV@yandex.ru',
                  Age: 19,
                  Married: false  
          }
]
    render() {

if(this.users.length > 0)

         return (
           <div>
                  {this.users.map((element) => ( //this так как это class. Мы тут обращаемся к классу users и задаем метод map (заранее известную), чтобы перебрать массив users. 
                           <div className="user" key={element.id} /*у каждого элемента из массива должно быть уникальное значение, в данном случае id*/ > 
                                    <h3>{element.Name} {element.Surname}</h3>
                                    <p>{element.Email}</p>
                           </div>
                  )
                  )
         }
           </div>
           )

           else
           return (
                  <div className="usersEmpty">
                        <h3>Пользователей не найдено</h3>
                  </div>
                  )

         }

        }


export default Users
