import React from "react";

const Button = (props) => { // const - это функция. В куруглых скобках (props) - в качестве параметра
         return (
           <button className="button1">
                  {props.text}
           </button> // обращаемся к параметру text
           )
         }

         Button.defaultProps = {
                  text: "Тык"
         }

export default Button
