import React from "react";
import Header from "./components/Header"
import Users from "./components/Users";
import AddUser from "./components/AddUser";


class App extends React.Component {

  constructor(props) { //для записи состояний
    super(props) // обязательная строка
    this.state = {
             users: [ //в таком варианте сам массив является состоянием
                      { // это массив, в котором есть 2 объекта
                               id: 1, /*id, Name и пр. это ключи*/ 
                               Name: 'Tommas',
                               Surname: 'Bang',
                               Email: 'tombang@yandex.ru',
                               Age: 34,
                               Married: true
                      },
                      {
                               id: 2,
                               Name: 'Nickole',
                               Surname: 'Vee',
                               Email: 'nickkiev@yandex.ru',
                               Age: 19,
                               Married: false  
                       }
             ]
    }
    this.addUser = this.addUser.bind(this)
    this.deleteUser = this.deleteUser.bind(this)
    this.editUser = this.editUser.bind(this)
}

  render() {
    return (<div> 
      <Header title= "Список пользователей"/>
        <main>
          <Users 
          onEdit={this.editUser}
          users={this.state.users} 
          onDelete={this.deleteUser}
          />
        </main>
        <aside>
          <AddUser onAdd={this.addUser} />
        </aside>
      </div>
    )
  }

deleteUser(id) {
  this.setState({
    users: this.state.users.filter((element) => element.id !== id)
  })
}

editUser(user) {
  let allUsers = this.state.users //Получили всех пользователей
  allUsers[user.id - 1] = user //Нашли нужного пользователя, заменили его (=user)
  
  this.setState({users: []}, () => { //очистили список
    this.setState({users: [...allUsers]}) //обновили список пздц
  })
}

addUser(user) {
  const id = this.state.users.length + 1
  this.setState({users: [...this.state.users, {id, ...user}]})
}

}

export default App
